# need to find a starter-of-pack marker in a datastream
# start of pack signal are four characters all different
def find_unique_length_pos(input, constraint):
    for i, letter in enumerate(input):
        if len(set(input[i:(i+constraint)])) == constraint:
            return i + constraint
        else:
            continue

def main():
    input = ""
    print("Start of packet marker:")
    print(find_unique_length_pos(input, 4))

    print("Start of message marker:")
    print(find_unique_length_pos(input, 14))
if __name__ == "__main__":
    main()
